import 'isomorphic-fetch';

const Channel = (props) => {
  const { channel } = props;
  return (
    <div>
      <header>Podcasts</header>
      <h1>{channel.title}</h1>
    </div>
  );
};

<style jsx>{`
  header {
    color: #fff;
    background: #8756ca;
    padding: 15px;
    text-align: center;
  }
  .channels {
    display: grid;
    grid-gap: 15px;
    padding: 15px;
    grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
  }
  a.channel {
    display: block;
    margin-bottom: 0.5em;
    color: #333;
    text-decoration: none;
  }
  .channel img {
    border-radius: 3px;
    box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
    width: 100%;
  }
  h2 {
    padding: 5px;
    font-size: 0.9em;
    font-weight: 600;
    margin: 0;
    text-align: center;
  }
`}</style>;

Channel.getInitialProps = async ({ query }) => {
  let idChannel = query.id;
  let req = await fetch(`https://api.audioboom.com/channels/${idChannel}`);
  let {
    body: { channel },
  } = await req.json();
  return { channel };
};

export default Channel;
