const Index = () => (
  <div>
    <header>Podcasts</header>

    <style jsx>{`
      header {
        color: #fff;
        background: #8756ca;
        padding: 15px;
        text-align: center;
      }
    `}</style>

    <style jsx global>{`
      body {
        margin: 0;
        font-family: system-ui;
        background: white;
      }
    `}</style>
  </div>
);

Index.getInitialProps = async (ctx) => {
  let req = await fetch('https://api.audioboom.com/channels/recommended');
  let { body: channels } = await req.json();
  return { channels };
};

export default Index;
