import 'isomorphic-fetch';

const Channel = () => {
  return <h1>Channel</h1>;
};

Channel.getInitialProps = async ({ query }) => {
  let idChannel = query.id;
  let req = await fetch(
    `https://api.audioboom.com/channels/recommended/${idChannel}`
  );
  let { body: channel } = await req.json();
  return { channel };
};

export default Channel;
